<xbeshAction type="file" filePath="src/context/DreamContext.tsx">import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { v4 as uuidv4 } from 'uuid';

export interface DreamEntry {
  id: string;
  content: string;
  emotions: string[];
  dreamSigns: string[];
  wasLucid: boolean;
  date: string;
}

interface DreamContextType {
  dreamEntries: DreamEntry[];
  addDreamEntry: (entry: Omit<DreamEntry, 'id'>) => void;
  updateDreamEntry: (id: string, entry: Partial<DreamEntry>) => void;
  deleteDreamEntry: (id: string) => void;
  getDreamSignFrequency: () => Record<string, number>;
  getEmotionFrequency: () => Record<string, number>;
  getLucidDreamCount: () => number;
}

const DreamContext = createContext<DreamContextType | undefined>(undefined);

export const DreamProvider = ({ children }: { children: ReactNode }) => {
  const [dreamEntries, setDreamEntries] = useState<DreamEntry[]>(() => {
    const savedEntries = localStorage.getItem('dreamEntries');
    return savedEntries ? JSON.parse(savedEntries) : [];
  });

  // Save dream entries to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('dreamEntries', JSON.stringify(dreamEntries));
  }, [dreamEntries]);

  const addDreamEntry = (entry: Omit<DreamEntry, 'id'>) => {
    const newEntry: DreamEntry = {
      id: uuidv4(),
      ...entry
    };

    setDreamEntries(prevEntries => [newEntry, ...prevEntries]);
  };

  const updateDreamEntry = (id: string, updates: Partial<DreamEntry>) => {
    setDreamEntries(prevEntries => 
      prevEntries.map(entry => 
        entry.id === id ? { ...entry, ...updates } : entry
      )
    );
  };

  const deleteDreamEntry = (id: string) => {
    setDreamEntries(prevEntries => prevEntries.filter(entry => entry.id !== id));
  };

  const getDreamSignFrequency = (): Record<string, number> => {
    const frequency: Record<string, number> = {};

    dreamEntries.forEach(entry => {
      entry.dreamSigns.forEach(sign => {
        frequency[sign] = (frequency[sign] || 0) + 1;
      });
    });

    return frequency;
  };

  const getEmotionFrequency = (): Record<string, number> => {
    const frequency: Record<string, number> = {};

    dreamEntries.forEach(entry => {
      entry.emotions.forEach(emotion => {
        frequency[emotion] = (frequency[emotion] || 0) + 1;
      });
    });

    return frequency;
  };

  const getLucidDreamCount = (): number => {
    return dreamEntries.filter(entry => entry.wasLucid).length;
  };

  return (
    <DreamContext.Provider value={{
      dreamEntries,
      addDreamEntry,
      updateDreamEntry,
      deleteDreamEntry,
      getDreamSignFrequency,
      getEmotionFrequency,
      getLucidDreamCount
    }}>
      {children}
    </DreamContext.Provider>
  );
};

export const useDream = (): DreamContextType => {
  const context = useContext(DreamContext);
  if (context === undefined) {
    throw new Error('useDream must be used within a DreamProvider');
  }
  return context;
};
</xbeshAction>

<xbeshAction type="file" filePath="src/context/RealityCheckContext.tsx">import { createContext, useContext, useState, useEffect, ReactNode } from 'react';

export interface RealityCheck {
  cue: string;
  action: string;
  completed: boolean;
  timestamp?: string;
}

interface RealityCheckContextType {
  realityChecks: RealityCheck[];
  addRealityCheck: (check: RealityCheck) => void;
  toggleRealityCheck: (index: number) => void;
  deleteRealityCheck: (index: number) => void;
  getCompletedCount: () => number;
  resetDailyChecks: () => void;
}

const RealityCheckContext = createContext<RealityCheckContextType | undefined>(undefined);

export const RealityCheckProvider = ({ children }: { children: ReactNode }) => {
  const [realityChecks, setRealityChecks] = useState<RealityCheck[]>(() => {
    const savedChecks = localStorage.getItem('realityChecks');
    return savedChecks ? JSON.parse(savedChecks) : [];
  });

  // Save reality checks to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('realityChecks', JSON.stringify(realityChecks));
  }, [realityChecks]);

  // Check if we need to reset daily checks
  useEffect(() => {
    const lastResetDate = localStorage.getItem('lastResetDate');
    const today = new Date().toDateString();

    if (lastResetDate !== today) {
      resetDailyChecks();
      localStorage.setItem('lastResetDate', today);
    }
  }, []);

  const addRealityCheck = (check: RealityCheck) => {
    setRealityChecks(prevChecks => [...prevChecks, check]);
  };

  const toggleRealityCheck = (index: number) => {
    setRealityChecks(prevChecks => 
      prevChecks.map((check, i) => 
        i === index 
          ? { 
              ...check, 
              completed: !check.completed,
              timestamp: !check.completed ? new Date().toISOString() : check.timestamp
            } 
          : check
      )
    );
  };

  const deleteRealityCheck = (index: number) => {
    setRealityChecks(prevChecks => prevChecks.filter((_, i) => i !== index));
  };

  const getCompletedCount = (): number => {
    return realityChecks.filter(check => check.completed).length;
  };

  const resetDailyChecks = () => {
    setRealityChecks(prevChecks => 
      prevChecks.map(check => ({ ...check, completed: false, timestamp: undefined }))
    );
  };

  return (
    <RealityCheckContext.Provider value={{
      realityChecks,
      addRealityCheck,
      toggleRealityCheck,
      deleteRealityCheck,
      getCompletedCount,
      resetDailyChecks
    }}>
      {children}
    </RealityCheckContext.Provider>
  );
};

export const useRealityCheck = (): RealityCheckContextType => {
  const context = useContext(RealityCheckContext);
  if (context === undefined) {
    throw new Error('useRealityCheck must be used within a RealityCheckProvider');
  }
  return context;
};
</xbeshAction>

<xbeshAction type="file" filePath="src/context/IntentionContext.tsx">import { createContext, useContext, useState, useEffect, ReactNode } from 'react';

interface IntentionContextType {
  intention: string;
  setIntention: (intention: string) => void;
  lastUpdated: string | null;
}

const IntentionContext = createContext<IntentionContextType | undefined>(undefined);

export const IntentionProvider = ({ children }: { children: ReactNode }) => {
  const [intention, setIntentionState] = useState<string>(() => {
    const savedIntention = localStorage.getItem('intention');
    return savedIntention || '';
  });

  const [lastUpdated, setLastUpdated] = useState<string | null>(() => {
    const savedLastUpdated = localStorage.getItem('intentionLastUpdated');
    return savedLastUpdated;
  });

  // Save intention to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('intention', intention);
  }, [intention]);

  // Save last updated timestamp
  useEffect(() => {
    if (lastUpdated) {
      localStorage.setItem('intentionLastUpdated', lastUpdated);
    }
  }, [lastUpdated]);

  const setIntention = (newIntention: string) => {
    setIntentionState(newIntention);
    setLastUpdated(new Date().toISOString());
  };

  return (
    <IntentionContext.Provider value={{
      intention,
      setIntention,
      lastUpdated
    }}>
      {children}
    </IntentionContext.Provider>
  );
};

export const useIntention = (): IntentionContextType => {
  const context = useContext(IntentionContext);
  if (context === undefined) {
    throw new Error('useIntention must be used within an IntentionProvider');
  }
  return context;
};
