<xbeshAction type="file" filePath="src/components/RealityPromptSettings.tsx">import { useState, useEffect } from 'react';
import { FiSettings, FiClock, FiBell, FiVolume2, FiVibrate, FiMessageCircle } from 'react-icons/fi';
import { motion } from 'framer-motion';
import { useRealityPrompt } from '../context/RealityPromptContext';
import '../styles/RealityPromptSettings.css';

const RealityPromptSettings = () => {
  const { 
    settings, 
    updateSettings,
    isEnabled,
    toggleEnabled
  } = useRealityPrompt();

  const [localSettings, setLocalSettings] = useState(settings);

  useEffect(() => {
    setLocalSettings(settings);
  }, [settings]);

  const handleSave = () => {
    updateSettings(localSettings);
  };

  const handlePromptCountChange = (count: number) => {
    setLocalSettings({
      ...localSettings,
      dailyReminderCount: count
    });
  };

  const handleAlertTypeChange = (type: string) => {
    setLocalSettings({
      ...localSettings,
      alertType: type
    });
  };

  const handleHourChange = (type: 'start' | 'end', value: string) => {
    setLocalSettings({
      ...localSettings,
      activeHours: {
        ...localSettings.activeHours,
        [type]: value
      }
    });
  };

  return (
    <div className="reality-prompt-settings">
      <div className="settings-header">
        <h3>
          <FiSettings className="settings-icon" />
          Reality Prompt Settings
        </h3>
        <div className="toggle-container">
          <label className="toggle-switch">
            <input 
              type="checkbox" 
              checked={isEnabled}
              onChange={() => toggleEnabled(!isEnabled)}
            />
            <span className="toggle-slider"></span>
          </label>
          <span className="toggle-label">{isEnabled ? 'Enabled' : 'Disabled'}</span>
        </div>
      </div>

      <div className="settings-content">
        <div className="settings-section">
          <h4>
            <FiBell />
            Daily Prompt Frequency
          </h4>
          <div className="prompt-count-options">
            {[10, 15, 20].map(count => (
              <button
                key={count}
                className={`count-option ${localSettings.dailyReminderCount === count ? 'selected' : ''}`}
                onClick={() => handlePromptCountChange(count)}
              >
                {count} times
              </button>
            ))}
          </div>
          <p className="setting-description">
            How many times per day would you like to be prompted to check reality?
          </p>
        </div>

        <div className="settings-section">
          <h4>
            <FiClock />
            Active Hours
          </h4>
          <div className="time-range">
            <div className="time-input">
              <label>Start</label>
              <input
                type="time"
                value={localSettings.activeHours.start}
                onChange={(e) => handleHourChange('start', e.target.value)}
              />
            </div>
            <div className="time-input">
              <label>End</label>
              <input
                type="time"
                value={localSettings.activeHours.end}
                onChange={(e) => handleHourChange('end', e.target.value)}
              />
            </div>
          </div>
          <p className="setting-description">
            Set your active hours when you want to receive reality check prompts.
          </p>
        </div>

        <div className="settings-section">
          <h4>
            <FiVolume2 />
            Alert Type
          </h4>
          <div className="alert-type-options">
            <button
              className={`alert-option ${localSettings.alertType === 'sound' ? 'selected' : ''}`}
              onClick={() => handleAlertTypeChange('sound')}
            >
              <FiVolume2 />
              Sound
            </button>
            <button
              className={`alert-option ${localSettings.alertType === 'vibrate' ? 'selected' : ''}`}
              onClick={() => handleAlertTypeChange('vibrate')}
            >
              <FiVibrate />
              Vibration
            </button>
            <button
              className={`alert-option ${localSettings.alertType === 'voice' ? 'selected' : ''}`}
              onClick={() => handleAlertTypeChange('voice')}
            >
              <FiMessageCircle />
              Voice
            </button>
          </div>
          <p className="setting-description">
            Choose how you want to be alerted for reality checks.
          </p>
        </div>

        <div className="settings-actions">
          <button className="primary" onClick={handleSave}>
            Save Settings
          </button>
        </div>
      </div>
    </div>
  );
};

export default RealityPromptSettings;
