  const [currentStep, setCurrentStep] = useState(0);
  const [note, setNote] = useState('');
  const [wasDreamlike, setWasDreamlike] = useState(false);
  const [completed, setCompleted] = useState(false);

  useEffect(() => {
    if (isOpen) {
      // Play sound based on settings
      const audio = new Audio('/notification.mp3');
      audio.play().catch(e => console.log('Audio play failed:', e));

      // Reset state when modal opens
      setCurrentStep(0);
      setNote('');
      setWasDreamlike(false);
      setCompleted(false);
    }
  }, [isOpen]);

  const stateTests = [
    {
      instruction: "Look at your hands â€” do they look normal?",
      description: "In dreams, hands often appear distorted or have the wrong number of fingers."
    },
    {
      instruction: "Pinch your nose and try to breathe",
      description: "In dreams, you can often still breathe even with your nose pinched closed."
    },
    {
      instruction: "What were you doing 5 minutes ago?",
      description: "In dreams, recent memory is often fragmented or nonsensical."
    },
    {
      instruction: "Try to push your finger through your palm",
      description: "In dreams, physical laws can be broken, and your finger might pass through."
    }
  ];

  const handleNextStep = () => {
    if (currentStep < stateTests.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      setCompleted(true);
    }
  };

  const handlePrevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleComplete = (result: 'normal' | 'dreamlike') => {
    logStateTest({
      timestamp: new Date().toISOString(),
      stateTestResult: result,
      note,
      wasDreamlike: result === 'dreamlike'
    });

    onClose();
  };

  const modalVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: { duration: 0.3 }
    },
    exit: { 
      opacity: 0, 
      scale: 0.8,
      transition: { duration: 0.2 }
    }
  };

  const backdropVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
    exit: { opacity: 0 }
  };

  const contentVariants = {
    hidden: { opacity: 0, x: 20 },
    visible: { opacity: 1, x: 0 },
    exit: { opacity: 0, x: -20 }
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div 
          className="modal-backdrop"
          variants={backdropVariants}
          initial="hidden"
          animate="visible"
          exit="exit"
        >
          <motion.div 
            className="state-test-modal"
            variants={modalVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <div className="modal-header">
              <h3>
                <FiBrain className="modal-icon" />
                Is This a Dream?
              </h3>
              <button className="close-button" onClick={onClose}>
                <FiX />
              </button>
            </div>

            <div className="modal-content">
              {!completed ? (
                <motion.div 
                  key={`step-${currentStep}`}
                  className="test-step"
                  variants={contentVariants}
                  initial="hidden"
                  animate="visible"
                  exit="exit"
                >
                  <div className="step-progress">
                    {stateTests.map((_, index) => (
                      <div 
                        key={index}
                        className={`progress-dot ${index === currentStep ? 'active' : ''} ${index < currentStep ? 'completed' : ''}`}
                      />
                    ))}
                  </div>

                  <h4>{stateTests[currentStep].instruction}</h4>
                  <p className="step-description">{stateTests[currentStep].description}</p>

                  <div className="step-actions">
                    {currentStep > 0 && (
                      <button className="secondary" onClick={handlePrevStep}>
                        Previous
                      </button>
                    )}
                    <button className="primary" onClick={handleNextStep}>
                      {currentStep < stateTests.length - 1 ? 'Next' : 'Complete Tests'}
                    </button>
                  </div>
                </motion.div>
              ) : (
                <motion.div 
                  className="test-results"
                  variants={contentVariants}
                  initial="hidden"
                  animate="visible"
                >
                  <h4>How did the reality check feel?</h4>

                  <div className="form-group">
                    <label htmlFor="test-note">Notes (optional):</label>
                    <textarea
                      id="test-note"
                      value={note}
                      onChange={(e) => setNote(e.target.value)}
                      placeholder="Add any observations or feelings..."
                      rows={3}
                    />
                  </div>

                  <div className="result-buttons">
                    <button 
                      className="result-button normal"
                      onClick={() => handleComplete('normal')}
                    >
                      <FiCheck />
                      <span>Reality is stable</span>
                      <small>Everything seems normal</small>
                    </button>

                    <button 
                      className="result-button dreamlike"
                      onClick={() => handleComplete('dreamlike')}
                    >
                      <FiAlertCircle />
                      <span>Something is off</span>
                      <small>Feels dreamlike or strange</small>
                    </button>
                  </div>
                </motion.div>
              )}
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default StateTestModal;
</xbeshAction>

<xbeshAction type="file" filePath="src/components/RealityPromptManager.tsx">import { useState, useEffect, useRef } from 'react';
import { FiBell } from 'react-icons/fi';
import { useRealityPrompt } from '../context/RealityPromptContext';
import StateTestModal from './StateTestModal';
import '../styles/RealityPromptManager.css';

const RealityPromptManager = () => {
  const { 
    settings, 
    isEnabled,
    stateTests,
    todaysTestCount
  } = useRealityPrompt();

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [nextPromptTime, setNextPromptTime] = useState<Date | null>(null);
  const timerRef = useRef<number | null>(null);

  // Calculate time until next prompt
  const scheduleNextPrompt = () => {
    if (!isEnabled) {
      setNextPromptTime(null);
      return;
    }

    // Parse active hours
    const startHour = parseInt(settings.activeHours.start.split(':')[0]);
    const startMinute = parseInt(settings.activeHours.start.split(':')[1]);
    const endHour = parseInt(settings.activeHours.end.split(':')[0]);
    const endMinute = parseInt(settings.activeHours.end.split(':')[1]);

    const now = new Date();
    const startTime = new Date(now);
    startTime.setHours(startHour, startMinute, 0, 0);

    const endTime = new Date(now);
    endTime.setHours(endHour, endMinute, 0, 0);

    // Check if current time is within active hours
    if (now < startTime) {
      // Before active hours, schedule for start time
      setNextPromptTime(startTime);
      return;
    }

    if (now > endTime) {
      // After active hours, schedule for tomorrow's start time
      const tomorrow = new Date(now);
      tomorrow.setDate(tomorrow.getDate() + 1);
      tomorrow.setHours(startHour, startMinute, 0, 0);
      setNextPromptTime(tomorrow);
      return;
    }

    // Within active hours, calculate next prompt time
    const activeMinutes = (endHour * 60 + endMinute) - (startHour * 60 + startMinute);
    const intervalMinutes = activeMinutes / settings.dailyReminderCount;

    // Add randomization (Â±10 minutes)
    const randomOffset = Math.floor(Math.random() * 21) - 10; // -10 to +10 minutes
    const nextIntervalMinutes = intervalMinutes + randomOffset;

    const nextTime = new Date(now.getTime() + nextIntervalMinutes * 60 * 1000);

    // Ensure next time is within active hours
    if (nextTime > endTime) {
      const tomorrow = new Date(now);
      tomorrow.setDate(tomorrow.getDate() + 1);
      tomorrow.setHours(startHour, startMinute, 0, 0);
      setNextPromptTime(tomorrow);
    } else {
      setNextPromptTime(nextTime);
    }
  };

  // Set up timer for next prompt
  useEffect(() => {
    if (!nextPromptTime || !isEnabled) return;

    const now = new Date();
    const timeUntilNextPrompt = nextPromptTime.getTime() - now.getTime();

    if (timerRef.current) {
      window.clearTimeout(timerRef.current);
    }

    if (timeUntilNextPrompt > 0) {
      timerRef.current = window.setTimeout(() => {
        triggerPrompt();
      }, timeUntilNextPrompt);
    } else {
      // If time is in the past, schedule next prompt immediately
      scheduleNextPrompt();
    }

    return () => {
      if (timerRef.current) {
        window.clearTimeout(timerRef.current);
      }
    };
  }, [nextPromptTime, isEnabled]);

  // Initial scheduling
  useEffect(() => {
    scheduleNextPrompt();

    // Check for missed prompts on page load/refresh
    const lastPromptTime = localStorage.getItem('lastPromptTime');
    if (lastPromptTime) {
      const lastTime = new Date(lastPromptTime);
      const now = new Date();

      // If it's been more than the interval since the last prompt, trigger one soon
      if ((now.getTime() - lastTime.getTime()) > 60 * 60 * 1000) { // 1 hour
        const soonTime = new Date(now.getTime() + 30 * 1000); // 30 seconds from now
        setNextPromptTime(soonTime);
      }
    }

    // Set up visibility change listener to handle tab switching
    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      if (timerRef.current) {
        window.clearTimeout(timerRef.current);
      }
    };
  }, [isEnabled, settings]);

  const handleVisibilityChange = () => {
    if (document.visibilityState === 'visible') {
      // Recalculate next prompt time when tab becomes visible again
      scheduleNextPrompt();
    }
  };

  const triggerPrompt = () => {
    // Record the time of this prompt
    localStorage.setItem('lastPromptTime', new Date().toISOString());

    // Show the modal
    setIsModalOpen(true);

    // Play notification sound based on settings
    if (settings.alertType === 'sound' || settings.alertType === 'voice') {
      const audio = new Audio('/notification.mp3');
      audio.play().catch(e => console.log('Audio play failed:', e));
    }

    // Vibrate if supported and selected
    if (settings.alertType === 'vibrate' && 'vibrate' in navigator) {
      navigator.vibrate(200);
    }

    // Schedule the next prompt
    scheduleNextPrompt();
  };

  const formatTimeUntilNext = () => {
    if (!nextPromptTime) return 'Not scheduled';

    const now = new Date();
    const diffMs = nextPromptTime.getTime() - now.getTime();

    if (diffMs <= 0) return 'Any moment now';

    const diffMinutes = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMinutes / 60);
    const remainingMinutes = diffMinutes % 60;

    if (diffHours > 0) {
      return `${diffHours}h ${remainingMinutes}m`;
    } else {
      return `${remainingMinutes}m`;
    }
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleManualTrigger = () => {
    triggerPrompt();
  };

  return (
    <div className="reality-prompt-manager">
      <div className="prompt-status">
        <div className="status-header">
          <h3>Reality Check Prompts</h3>
          <span className={`status-indicator ${isEnabled ? 'active' : 'inactive'}`}>
            {isEnabled ? 'Active' : 'Inactive'}
          </span>
        </div>

        {isEnabled && (
          <div className="prompt-info">
            <div className="info-item">
              <span className="info-label">Next prompt:</span>
              <span className="info-value">{formatTimeUntilNext()}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Today's checks:</span>
              <span className="info-value">{todaysTestCount} of {settings.dailyReminderCount}</span>
            </div>
            <button className="secondary trigger-button" onClick={handleManualTrigger}>
              <FiBell />
              Trigger Now
            </button>
          </div>
        )}
      </div>

      <StateTestModal isOpen={isModalOpen} onClose={handleCloseModal} />
    </div>
  );
};

export default RealityPromptManager;
</xbeshAction>

<xbeshAction type="file" filePath="src/components/RealityPromptStats.tsx">import { useState } from 'react';
import { FiBarChart2, FiCalendar, FiClock } from 'react-icons/fi';
import { motion } from 'framer-motion';
import { useRealityPrompt } from '../context/RealityPromptContext';
import '../styles/RealityPromptStats.css';

const RealityPromptStats = () => {
  const { stateTests, settings } = useRealityPrompt();
  const [timeframe, setTimeframe] = useState<'day' | 'week' | 'month'>('week');

  // Filter tests based on timeframe
  const getFilteredTests = () => {
    const now = new Date();
    const cutoffDate = new Date();

    switch (timeframe) {
      case 'day':
        cutoffDate.setDate(now.getDate() - 1);
        break;
      case 'week':
        cutoffDate.setDate(now.getDate() - 7);
        break;
      case 'month':
        cutoffDate.setMonth(now.getMonth() - 1);
        break;
    }

    return stateTests.filter(test => new Date(test.timestamp) >= cutoffDate);
  };

  const filteredTests = getFilteredTests();

  // Calculate completion rate
  const calculateCompletionRate = () => {
    if (filteredTests.length === 0) return 0;

    const daysInTimeframe = timeframe === 'day' ? 1 : timeframe === 'week' ? 7 : 30;
    const expectedTotal = settings.dailyReminderCount * daysInTimeframe;

    return Math.min(100, Math.round((filteredTests.length / expectedTotal) * 100));
  };

  // Calculate dreamlike percentage
  const calculateDreamlikePercentage = () => {
    if (filteredTests.length === 0) return 0;

    const dreamlikeCount = filteredTests.filter(test => test.wasDreamlike).length;
    return Math.round((dreamlikeCount / filteredTests.length) * 100);
  };

  // Group tests by day for the chart
  const getChartData = () => {
    const data: Record<string, number> = {};

    // Initialize days
    const now = new Date();
    const daysToShow = timeframe === 'day' ? 1 : timeframe === 'week' ? 7 : 30;

    for (let i = 0; i < daysToShow; i++) {
      const date = new Date(now);
      date.setDate(date.getDate() - i);
      const dateString = date.toISOString().split('T')[0];
      data[dateString] = 0;
    }

    // Count tests per day
    filteredTests.forEach(test => {
      const dateString = test.timestamp.split('T')[0];
      if (data[dateString] !== undefined) {
        data[dateString]++;
      }
    });

    // Convert to array and sort by date
    return Object.entries(data)
      .map(([date, count]) => ({ date, count }))
      .sort((a, b) => a.date.localeCompare(b.date));
  };

  const chartData = getChartData();
  const maxCount = Math.max(...chartData.map(item => item.count), settings.dailyReminderCount);

  // Format date for display
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString(undefined, { 
      month: 'short', 
      day: 'numeric' 
    });
  };

  return (
    <div className="reality-prompt-stats">
      <div className="stats-header">
        <h3>
          <FiBarChart2 className="stats-icon" />
          Reality Check Stats
        </h3>
        <div className="timeframe-selector">
          <button 
            className={`timeframe-button ${timeframe === 'day' ? 'active' : ''}`}
            onClick={() => setTimeframe('day')}
          >
            Day
          </button>
          <button 
            className={`timeframe-button ${timeframe === 'week' ? 'active' : ''}`}
            onClick={() => setTimeframe('week')}
          >
            Week
          </button>
          <button 
            className={`timeframe-button ${timeframe === 'month' ? 'active' : ''}`}
            onClick={() => setTimeframe('month')}
          >
            Month
          </button>
        </div>
      </div>

      <div className="stats-content">
        <div className="stats-summary">
          <div className="stat-card">
            <div className="stat-value">{filteredTests.length}</div>
            <div className="stat-label">Checks Completed</div>
          </div>

          <div className="stat-card">
            <div className="stat-value">{calculateCompletionRate()}%</div>
            <div className="stat-label">Completion Rate</div>
          </div>

          <div className="stat-card">
            <div className="stat-value">{calculateDreamlikePercentage()}%</div>
            <div className="stat-label">Dreamlike Experiences</div>
          </div>
        </div>

        <div className="stats-chart">
          <h4>
            <FiCalendar />
            Check Frequency
          </h4>
          <div className="chart-container">
            <div className="chart-target-line" style={{ bottom: `${(settings.dailyReminderCount / maxCount) * 100}%` }}>
              <span className="target-label">Target: {settings.dailyReminderCount}/day</span>
            </div>
            <div className="bar-chart">
              {chartData.map((item, index) => (
                <div key={index} className="chart-bar-container">
                  <div className="chart-date">{formatDate(item.date)}</div>
                  <div className="chart-bar-wrapper">
                    <motion.div 
                      className={`chart-bar ${item.count >= settings.dailyReminderCount ? 'complete' : ''}`}
                      initial={{ height: 0 }}
                      animate={{ height: `${(item.count / maxCount) * 100}%` }}
                      transition={{ duration: 0.5, delay: index * 0.05 }}
                    />
                  </div>
                  <div className="chart-value">{item.count}</div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {filteredTests.length > 0 && (
          <div className="recent-tests">
            <h4>
              <FiClock />
              Recent Checks
            </h4>
            <div className="test-list">
              {filteredTests.slice(0, 5).map((test, index) => (
                <div key={index} className={`test-item ${test.wasDreamlike ? 'dreamlike' : ''}`}>
                  <div className="test-time">
                    {new Date(test.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </div>
                  <div className="test-result">
                    {test.wasDreamlike ? 'Dreamlike' : 'Normal'}
                  </div>
                  {test.note && (
                    <div className="test-note">{test.note}</div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RealityPromptStats;
</xbeshAction>

<xbeshAction type="file" filePath="src/context/RealityPromptContext.tsx">import { createContext, useContext, useState, useEffect, ReactNode } from 'react';

export interface RealityPromptSettings {
  dailyReminderCount: number;
  alertType: string;
  activeHours: {
    start: string;
    end: string;
  };
}

export interface StateTest {
  timestamp: string;
  stateTestResult: string;
  note: string;
  wasDreamlike: boolean;
}

interface RealityPromptContextType {
  settings: RealityPromptSettings;
  updateSettings: (settings: RealityPromptSettings) => void;
  isEnabled: boolean;
  toggleEnabled: (enabled: boolean) => void;
  stateTests: StateTest[];
  logStateTest: (test: StateTest) => void;
  todaysTestCount: number;
}

const defaultSettings: RealityPromptSettings = {
  dailyReminderCount: 10,
  alertType: 'sound',
  activeHours: {
    start: '08:00',
    end: '22:00'
  }
};

const RealityPromptContext = createContext<RealityPromptContextType | undefined>(undefined);

export const RealityPromptProvider = ({ children }: { children: ReactNode }) => {
  const [settings, setSettings] = useState<RealityPromptSettings>(() => {
    const savedSettings = localStorage.getItem('realityPromptSettings');
    return savedSettings ? JSON.parse(savedSettings) : defaultSettings;
  });

  const [isEnabled, setIsEnabled] = useState<boolean>(() => {
    const savedEnabled = localStorage.getItem('realityPromptEnabled');
    return savedEnabled ? JSON.parse(savedEnabled) : false;
  });

  const [stateTests, setStateTests] = useState<StateTest[]>(() => {
    const savedTests = localStorage.getItem('stateTests');
    return savedTests ? JSON.parse(savedTests) : [];
  });

  // Save settings to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('realityPromptSettings', JSON.stringify(settings));
  }, [settings]);

  // Save enabled state to localStorage
  useEffect(() => {
    localStorage.setItem('realityPromptEnabled', JSON.stringify(isEnabled));
  }, [isEnabled]);

  // Save state tests to localStorage
  useEffect(() => {
    localStorage.setItem('stateTests', JSON.stringify(stateTests));
  }, [stateTests]);

  const updateSettings = (newSettings: RealityPromptSettings) => {
    setSettings(newSettings);
  };

  const toggleEnabled = (enabled: boolean) => {
    setIsEnabled(enabled);
  };

  const logStateTest = (test: StateTest) => {
    setStateTests(prevTests => [test, ...prevTests]);
  };

  // Calculate today's test count
  const getTodaysTestCount = (): number => {
    const today = new Date().toISOString().split('T')[0];
    return stateTests.filter(test => test.timestamp.startsWith(today)).length;
  };

  return (
    <RealityPromptContext.Provider value={{
      settings,
      updateSettings,
      isEnabled,
      toggleEnabled,
      stateTests,
      logStateTest,
      todaysTestCount: getTodaysTestCount()
    }}>
      {children}
    </RealityPromptContext.Provider>
  );
};

export const useRealityPrompt = (): RealityPromptContextType => {
  const context = useContext(RealityPromptContext);
  if (context === undefined) {
    throw new Error('useRealityPrompt must be used within a RealityPromptProvider');
  }
  return context;
};
</xbeshAction>

