<xbeshAction type="file" filePath="src/components/DreamingDashboard.tsx">import { useState } from 'react';
import { FiMoon, FiSun, FiAlertCircle, FiSettings, FiBarChart2 } from 'react-icons/fi';
import { motion } from 'framer-motion';
import { useDream } from '../context/DreamContext';
import { useRealityCheck } from '../context/RealityCheckContext';
import { useRealityPrompt } from '../context/RealityPromptContext';
import '../styles/DreamingDashboard.css';

const DreamingDashboard = () => {
  const { dreamEntries, getLucidDreamCount } = useDream();
  const { realityChecks, getCompletedCount } = useRealityCheck();
  const { todaysTestCount, settings } = useRealityPrompt();

  const [activeTab, setActiveTab] = useState<'overview' | 'settings' | 'stats'>('overview');

  // Calculate lucid dream percentage
  const calculateLucidPercentage = () => {
    if (dreamEntries.length === 0) return 0;
    return Math.round((getLucidDreamCount() / dreamEntries.length) * 100);
  };

  // Calculate reality check completion percentage
  const calculateRealityCheckPercentage = () => {
    if (realityChecks.length === 0) return 0;
    return Math.min(100, Math.round((getCompletedCount() / realityChecks.length) * 100));
  };

  // Calculate prompt completion percentage
  const calculatePromptPercentage = () => {
    return Math.min(100, Math.round((todaysTestCount / settings.dailyReminderCount) * 100));
  };

  return (
    <div className="dreaming-dashboard">
      <div className="dashboard-tabs">
        <button 
          className={`dashboard-tab ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          <FiMoon />
          Overview
        </button>
        <button 
          className={`dashboard-tab ${activeTab === 'settings' ? 'active' : ''}`}
          onClick={() => setActiveTab('settings')}
        >
          <FiSettings />
          Settings
        </button>
        <button 
          className={`dashboard-tab ${activeTab === 'stats' ? 'active' : ''}`}
          onClick={() => setActiveTab('stats')}
        >
          <FiBarChart2 />
          Statistics
        </button>
      </div>

      <div className="dashboard-content">
        {activeTab === 'overview' && (
          <motion.div 
            className="overview-tab"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <div className="stats-grid">
              <div className="stat-box">
                <div className="stat-icon dream-icon">
                  <FiMoon />
                </div>
                <div className="stat-info">
                  <div className="stat-value">{dreamEntries.length}</div>
                  <div className="stat-label">Dreams Recorded</div>
                </div>
                <div className="stat-extra">
                  <div className="stat-percentage">{calculateLucidPercentage()}%</div>
                  <div className="stat-sublabel">Lucid</div>
                </div>
              </div>

              <div className="stat-box">
                <div className="stat-icon check-icon">
                  <FiAlertCircle />
                </div>
                <div className="stat-info">
                  <div className="stat-value">{realityChecks.length}</div>
                  <div className="stat-label">Reality Checks</div>
                </div>
                <div className="stat-extra">
                  <div className="stat-percentage">{calculateRealityCheckPercentage()}%</div>
                  <div className="stat-sublabel">Completed Today</div>
                </div>
              </div>

              <div className="stat-box">
                <div className="stat-icon prompt-icon">
                  <FiBell />
                </div>
                <div className="stat-info">
                  <div className="stat-value">{todaysTestCount}</div>
                  <div className="stat-label">Prompts Today</div>
                </div>
                <div className="stat-extra">
                  <div className="stat-percentage">{calculatePromptPercentage()}%</div>
                  <div className="stat-sublabel">of Daily Goal</div>
                </div>
              </div>
            </div>

            <div className="dashboard-cards">
              <div className="dashboard-card">
                <h4>
                  <FiSun className="card-icon morning" />
                  Morning Routine
                </h4>
                <ul className="routine-list">
                  <li>Record your dreams immediately upon waking</li>
                  <li>Note any dream signs or recurring patterns</li>
                  <li>Reflect on whether you had any moments of lucidity</li>
                </ul>
              </div>

              <div className="dashboard-card">
                <h4>
                  <FiAlertCircle className="card-icon day" />
                  Daytime Practice
                </h4>
                <ul className="routine-list">
                  <li>Perform reality checks throughout the day</li>
                  <li>Pay attention to "Is this a dream?" prompts</li>
                  <li>Practice mindfulness and present-moment awareness</li>
                </ul>
              </div>

              <div className="dashboard-card">
                <h4>
                  <FiMoon className="card-icon night" />
                  Evening Routine
                </h4>
                <ul className="routine-list">
                  <li>Set your intention for tonight's dreams</li>
                  <li>Practice the MILD technique before sleep</li>
                  <li>Visualize becoming lucid in a recent dream</li>
                </ul>
              </div>
            </div>
          </motion.div>
        )}

        {activeTab === 'settings' && (
          <motion.div 
            className="settings-tab"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <RealityPromptSettings />
          </motion.div>
        )}

        {activeTab === 'stats' && (
          <motion.div 
            className="stats-tab"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <RealityPromptStats />
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default DreamingDashboard;
</xbeshAction>

