<xbeshAction type="file" filePath="src/App.tsx">import { useEffect, useState } from 'react'
import { useTheme } from './context/ThemeContext'
import Header from './components/Header'
import Dashboard from './components/Dashboard'
import HabitList from './components/HabitList'
import AddHabitForm from './components/AddHabitForm'
import { useHabit } from './context/HabitContext'
import MotivationCard from './components/MotivationCard'
import DreamJournal from './components/DreamJournal'
import RealityChecks from './components/RealityChecks'
import IntentionSetting from './components/IntentionSetting'
import MILDTechnique from './components/MILDTechnique'
import DreamSignsAnalysis from './components/DreamSignsAnalysis'
import DreamingDashboard from './components/DreamingDashboard'
import RealityPromptManager from './components/RealityPromptManager'
import StateTestModal from './components/StateTestModal'
import { motion } from 'framer-motion'
import { FiActivity, FiMoon, FiSun } from 'react-icons/fi'

function App() {
  const { theme } = useTheme()
  const { habits } = useHabit()
  const [activeTab, setActiveTab] = useState<'habits' | 'dreams'>('habits')
  const [isTestModalOpen, setIsTestModalOpen] = useState(false)

  // Apply theme class to body
  useEffect(() => {
    document.body.className = theme
  }, [theme])

  return (
    <div className="app">
      <Header />
      <main className="container">
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Dashboard />
        </motion.div>

        <div className="tab-navigation">
          <button 
            className={`tab-button ${activeTab === 'habits' ? 'active' : ''}`}
            onClick={() => setActiveTab('habits')}
          >
            <FiActivity /> Habit Tracker
          </button>
          <button 
            className={`tab-button ${activeTab === 'dreams' ? 'active' : ''}`}
            onClick={() => setActiveTab('dreams')}
          >
            <FiMoon /> Lucid Dreaming
          </button>
        </div>

        {activeTab === 'habits' ? (
          <div className="content-grid">
            <motion.div 
              className="habit-section"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.1 }}
            >
              <h2>Your Habits</h2>
              {habits.length > 0 ? (
                <HabitList />
              ) : (
                <div className="empty-state">
                  <p>You haven't added any habits yet. Start tracking your progress today!</p>
                </div>
              )}
              <AddHabitForm />
            </motion.div>

            <motion.div 
              className="motivation-section"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <h2>Motivation</h2>
              <MotivationCard />
            </motion.div>
          </div>
        ) : (
          <>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <DreamingDashboard />
              <RealityPromptManager />
            </motion.div>

            <div className="content-grid">
              <motion.div 
                className="dream-section"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.1 }}
              >
                <h2>
                  <FiSun className="section-icon morning" /> Morning Reflection
                </h2>
                <DreamJournal />

                <h2>
                  <FiSun className="section-icon day" /> Daytime Awareness
                </h2>
                <RealityChecks />
              </motion.div>

              <motion.div 
                className="evening-section"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.2 }}
              >
                <h2>
                  <FiMoon className="section-icon evening" /> Evening Practice
                </h2>
                <IntentionSetting />
                <MILDTechnique />

                <h2>Dream Analysis</h2>
                <DreamSignsAnalysis />
              </motion.div>
            </div>
          </>
        )}
      </main>

      <StateTestModal isOpen={isTestModalOpen} onClose={() => setIsTestModalOpen(false)} />
    </div>
  )
}

export default App
</xbeshAction>

