<xbeshAction type="file" filePath="src/components/StateTestModal.tsx">import { useState, useEffect } from 'react';
import { FiX, FiCheck, FiAlertCircle, FiActivity } from 'react-icons/fi';
import { motion, AnimatePresence } from 'framer-motion';
import { useRealityPrompt } from '../context/RealityPromptContext';
import '../styles/StateTestModal.css';

interface StateTestModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const StateTestModal = ({ isOpen, onClose }: StateTestModalProps) => {
  const { logStateTest } = useRealityPrompt();
  const [currentStep, setCurrentStep] = useState(0);
  const [note, setNote] = useState('');
  const [wasDreamlike, setWasDreamlike] = useState(false);
  const [completed, setCompleted] = useState(false);

  useEffect(() => {
    if (isOpen) {
      // Play sound based on settings
      const audio = new Audio('/notification.mp3');
      audio.play().catch(e => console.log('Audio play failed:', e));

      // Reset state when modal opens
      setCurrentStep(0);
      setNote('');
      setWasDreamlike(false);
      setCompleted(false);
    }
  }, [isOpen]);

  const stateTests = [
    {
      instruction: "Look at your hands â€” do they look normal?",
      description: "In dreams, hands often appear distorted or have the wrong number of fingers."
    },
    {
      instruction: "Pinch your nose and try to breathe",
      description: "In dreams, you can often still breathe even with your nose pinched closed."
    },
    {
      instruction: "What were you doing 5 minutes ago?",
      description: "In dreams, recent memory is often fragmented or nonsensical."
    },
    {
      instruction: "Try to push your finger through your palm",
      description: "In dreams, physical laws can be broken, and your finger might pass through."
    }
  ];

  const handleNextStep = () => {
    if (currentStep < stateTests.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      setCompleted(true);
    }
  };

  const handlePrevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleComplete = (result: 'normal' | 'dreamlike') => {
    logStateTest({
      timestamp: new Date().toISOString(),
      stateTestResult: result,
      note,
      wasDreamlike: result === 'dreamlike'
    });

    onClose();
  };

  const modalVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: { duration: 0.3 }
    },
    exit: { 
      opacity: 0, 
      scale: 0.8,
      transition: { duration: 0.2 }
    }
  };

  const backdropVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
    exit: { opacity: 0 }
  };

  const contentVariants = {
    hidden: { opacity: 0, x: 20 },
    visible: { opacity: 1, x: 0 },
    exit: { opacity: 0, x: -20 }
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div 
          className="modal-backdrop"
          variants={backdropVariants}
          initial="hidden"
          animate="visible"
          exit="exit"
        >
          <motion.div 
            className="state-test-modal"
            variants={modalVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <div className="modal-header">
              <h3>
                <FiActivity className="modal-icon" />
                Is This a Dream?
              </h3>
              <button className="close-button" onClick={onClose}>
                <FiX />
              </button>
            </div>

            <div className="modal-content">
              {!completed ? (
                <motion.div 
                  key={`step-${currentStep}`}
                  className="test-step"
                  variants={contentVariants}
                  initial="hidden"
                  animate="visible"
                  exit="exit"
                >
                  <div className="step-progress">
                    {stateTests.map((_, index) => (
                      <div 
                        key={index}
                        className={`progress-dot ${index === currentStep ? 'active' : ''} ${index < currentStep ? 'completed' : ''}`}
                      />
                    ))}
                  </div>

                  <h4>{stateTests[currentStep].instruction}</h4>
                  <p className="step-description">{stateTests[currentStep].description}</p>

                  <div className="step-actions">
                    {currentStep > 0 && (
                      <button className="secondary" onClick={handlePrevStep}>
                        Previous
                      </button>
                    )}
                    <button className="primary" onClick={handleNextStep}>
                      {currentStep < stateTests.length - 1 ? 'Next' : 'Complete Tests'}
                    </button>
                  </div>
                </motion.div>
              ) : (
                <motion.div 
                  className="test-results"
                  variants={contentVariants}
                  initial="hidden"
                  animate="visible"
                >
                  <h4>How did the reality check feel?</h4>

                  <div className="form-group">
                    <label htmlFor="test-note">Notes (optional):</label>
                    <textarea
                      id="test-note"
                      value={note}
                      onChange={(e) => setNote(e.target.value)}
                      placeholder="Add any observations or feelings..."
                      rows={3}
                    />
                  </div>

                  <div className="result-buttons">
                    <button 
                      className="result-button normal"
                      onClick={() => handleComplete('normal')}
                    >
                      <FiCheck />
                      <span>Reality is stable</span>
                      <small>Everything seems normal</small>
                    </button>

                    <button 
                      className="result-button dreamlike"
                      onClick={() => handleComplete('dreamlike')}
                    >
                      <FiAlertCircle />
                      <span>Something is off</span>
                      <small>Feels dreamlike or strange</small>
                    </button>
                  </div>
                </motion.div>
              )}
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default StateTestModal;
