<xbeshAction type="file" filePath="src/components/ContentManager.tsx">import { useState } from 'react';
import { FiVideo, FiFileText, FiPlus, FiEdit2, FiTrash2 } from 'react-icons/fi';
import { motion } from 'framer-motion';
import '../styles/ContentManager.css';

interface ContentItem {
  id: string;
  title: string;
  description: string;
  type: 'video' | 'pdf';
  url: string;
  category: 'meditation' | 'lucid-dreaming' | 'dream-yoga' | 'didgeridoo';
  duration?: string;
  level?: 'beginner' | 'intermediate' | 'advanced';
}

const ContentManager = () => {
  const [contentItems, setContentItems] = useState<ContentItem[]>(() => {
    const savedItems = localStorage.getItem('educationalContent');
    return savedItems ? JSON.parse(savedItems) : [];
  });

  const [isAddingItem, setIsAddingItem] = useState(false);
  const [editingItemId, setEditingItemId] = useState<string | null>(null);
  const [formData, setFormData] = useState<Omit<ContentItem, 'id'>>({
    title: '',
    description: '',
    type: 'video',
    url: '',
    category: 'meditation',
    duration: '',
    level: 'beginner'
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingItemId) {
      // Update existing item
      const updatedItems = contentItems.map(item => 
        item.id === editingItemId ? { ...formData, id: editingItemId } : item
      );
      setContentItems(updatedItems);
      localStorage.setItem('educationalContent', JSON.stringify(updatedItems));
      setEditingItemId(null);
    } else {
      // Add new item
      const newItem: ContentItem = {
        ...formData,
        id: Date.now().toString()
      };
      const newItems = [...contentItems, newItem];
      setContentItems(newItems);
      localStorage.setItem('educationalContent', JSON.stringify(newItems));
    }

    // Reset form
    setFormData({
      title: '',
      description: '',
      type: 'video',
      url: '',
      category: 'meditation',
      duration: '',
      level: 'beginner'
    });
    setIsAddingItem(false);
  };

  const handleEdit = (item: ContentItem) => {
    setFormData({
      title: item.title,
      description: item.description,
      type: item.type,
      url: item.url,
      category: item.category,
      duration: item.duration || '',
      level: item.level || 'beginner'
    });
    setEditingItemId(item.id);
    setIsAddingItem(true);
  };

  const handleDelete = (id: string) => {
    const updatedItems = contentItems.filter(item => item.id !== id);
    setContentItems(updatedItems);
    localStorage.setItem('educationalContent', JSON.stringify(updatedItems));
  };

  const handleCancel = () => {
    setIsAddingItem(false);
    setEditingItemId(null);
    setFormData({
      title: '',
      description: '',
      type: 'video',
      url: '',
      category: 'meditation',
      duration: '',
      level: 'beginner'
    });
  };

  return (
    <div className="content-manager">
      <div className="manager-header">
        <h2>Manage Educational Content</h2>
        {!isAddingItem && (
          <button className="add-content-btn" onClick={() => setIsAddingItem(true)}>
            <FiPlus /> Add New Content
          </button>
        )}
      </div>

      {isAddingItem ? (
        <motion.div 
          className="content-form"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
        >
          <h3>{editingItemId ? 'Edit Content' : 'Add New Content'}</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="type">Content Type</label>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleInputChange}
                >
                  <option value="video">YouTube Video</option>
                  <option value="pdf">PDF Document</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="category">Category</label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                >
                  <option value="meditation">Meditation</option>
                  <option value="lucid-dreaming">Lucid Dreaming</option>
                  <option value="dream-yoga">Dream Yoga</option>
                  <option value="didgeridoo">Didgeridoo</option>
                </select>
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="url">
                {formData.type === 'video' ? 'YouTube Embed URL' : 'PDF URL'}
              </label>
              <input
                type="url"
                id="url"
                name="url"
                value={formData.url}
                onChange={handleInputChange}
                placeholder={formData.type === 'video' 
                  ? 'https://www.youtube.com/embed/VIDEO_ID' 
                  : 'https://example.com/document.pdf'}
                required
              />
              {formData.type === 'video' && (
                <p className="form-hint">
                  Use the embed URL format: https://www.youtube.com/embed/VIDEO_ID
                </p>
              )}
            </div>

            <div className="form-row">
              {formData.type === 'video' && (
                <div className="form-group">
                  <label htmlFor="duration">Duration (e.g., "15 min")</label>
                  <input
                    type="text"
                    id="duration"
                    name="duration"
                    value={formData.duration}
                    onChange={handleInputChange}
                    placeholder="15 min"
                  />
                </div>
              )}

              <div className="form-group">
                <label htmlFor="level">Difficulty Level</label>
                <select
                  id="level"
                  name="level"
                  value={formData.level}
                  onChange={handleInputChange}
                >
                  <option value="beginner">Beginner</option>
                  <option value="intermediate">Intermediate</option>
                  <option value="advanced">Advanced</option>
                </select>
              </div>
            </div>

            <div className="form-actions">
              <button type="button" className="cancel-btn" onClick={handleCancel}>
                Cancel
              </button>
              <button type="submit" className="save-btn">
                {editingItemId ? 'Update Content' : 'Add Content'}
              </button>
            </div>
          </form>
        </motion.div>
      ) : (
        <div className="content-items-list">
          {contentItems.length > 0 ? (
            contentItems.map(item => (
              <motion.div 
                key={item.id}
                className="content-item-card"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
              >
                <div className="item-icon">
                  {item.type === 'video' ? <FiVideo /> : <FiFileText />}
                </div>
                <div className="item-details">
                  <h3>{item.title}</h3>
                  <div className="item-meta">
                    <span className={`item-category ${item.category}`}>
                      {item.category.replace('-', ' ')}
                    </span>
                    {item.duration && <span className="item-duration">{item.duration}</span>}
                    {item.level && (
                      <span className={`item-level ${item.level}`}>
                        {item.level}
                      </span>
                    )}
                  </div>
                  <p className="item-description">{item.description}</p>
                </div>
                <div className="item-actions">
                  <button className="edit-btn" onClick={() => handleEdit(item)}>
                    <FiEdit2 />
                  </button>
                  <button className="delete-btn" onClick={() => handleDelete(item.id)}>
                    <FiTrash2 />
                  </button>
                </div>
              </motion.div>
            ))
          ) : (
            <div className="no-content-message">
              <p>No custom content added yet. Click "Add New Content" to get started.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ContentManager;
</xbeshAction>

