<xbeshAction type="file" filePath="src/context/RealityPromptContext.tsx">import { createContext, useContext, useState, useEffect, ReactNode } from 'react';

interface RealityPromptSettings {
  dailyReminderCount: number;
  alertType: 'sound' | 'vibrate' | 'voice';
  activeHours: {
    start: string;
    end: string;
  };
}

interface StateTest {
  id: string;
  timestamp: string;
  result: 'passed' | 'failed';
  notes?: string;
}

interface RealityPromptContextType {
  settings: RealityPromptSettings;
  updateSettings: (newSettings: RealityPromptSettings) => void;
  isEnabled: boolean;
  toggleEnabled: (value: boolean) => void;
  stateTests: StateTest[];
  addStateTest: (test: Omit<StateTest, 'id' | 'timestamp'>) => void;
  todaysTestCount: number;
}

const RealityPromptContext = createContext<RealityPromptContextType | undefined>(undefined);

const DEFAULT_SETTINGS: RealityPromptSettings = {
  dailyReminderCount: 10,
  alertType: 'sound',
  activeHours: {
    start: '08:00',
    end: '22:00'
  }
};

export const RealityPromptProvider = ({ children }: { children: ReactNode }) => {
  const [settings, setSettings] = useState<RealityPromptSettings>(() => {
    const savedSettings = localStorage.getItem('realityPromptSettings');
    return savedSettings ? JSON.parse(savedSettings) : DEFAULT_SETTINGS;
  });

  const [isEnabled, setIsEnabled] = useState<boolean>(() => {
    const savedEnabled = localStorage.getItem('realityPromptEnabled');
    return savedEnabled ? JSON.parse(savedEnabled) : false;
  });

  const [stateTests, setStateTests] = useState<StateTest[]>(() => {
    const savedTests = localStorage.getItem('stateTests');
    return savedTests ? JSON.parse(savedTests) : [];
  });

  // Calculate today's test count
  const [todaysTestCount, setTodaysTestCount] = useState<number>(0);

  useEffect(() => {
    // Calculate today's test count
    const today = new Date().toISOString().split('T')[0];
    const todayTests = stateTests.filter(test => 
      test.timestamp.startsWith(today)
    );
    setTodaysTestCount(todayTests.length);
  }, [stateTests]);

  // Save settings to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('realityPromptSettings', JSON.stringify(settings));
  }, [settings]);

  // Save enabled state to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('realityPromptEnabled', JSON.stringify(isEnabled));
  }, [isEnabled]);

  // Save state tests to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('stateTests', JSON.stringify(stateTests));
  }, [stateTests]);

  const updateSettings = (newSettings: RealityPromptSettings) => {
    setSettings(newSettings);
  };

  const toggleEnabled = (value: boolean) => {
    setIsEnabled(value);
  };

  const addStateTest = (test: Omit<StateTest, 'id' | 'timestamp'>) => {
    const newTest: StateTest = {
      id: Date.now().toString(),
      timestamp: new Date().toISOString(),
      ...test
    };

    setStateTests(prevTests => [newTest, ...prevTests]);
  };

  return (
    <RealityPromptContext.Provider value={{
      settings,
      updateSettings,
      isEnabled,
      toggleEnabled,
      stateTests,
      addStateTest,
      todaysTestCount
    }}>
      {children}
    </RealityPromptContext.Provider>
  );
};

export const useRealityPrompt = (): RealityPromptContextType => {
  const context = useContext(RealityPromptContext);
  if (context === undefined) {
    throw new Error('useRealityPrompt must be used within a RealityPromptProvider');
  }
  return context;
};
</xbeshAction>

<xbeshAction type="file" filePath="src/components/RealityPromptStats.tsx">import { useState } from 'react';
import { FiBarChart2, FiCalendar, FiClock } from 'react-icons/fi';
import { useRealityPrompt } from '../context/RealityPromptContext';
import { format, subDays, isWithinInterval, parseISO } from 'date-fns';
import '../styles/RealityPromptStats.css';

const RealityPromptStats = () => {
  const { stateTests } = useRealityPrompt();
  const [timeRange, setTimeRange] = useState<'week' | 'month' | 'all'>('week');

  // Filter tests based on selected time range
  const getFilteredTests = () => {
    const now = new Date();

    if (timeRange === 'week') {
      const weekAgo = subDays(now, 7);
      return stateTests.filter(test => 
        isWithinInterval(parseISO(test.timestamp), { start: weekAgo, end: now })
      );
    } else if (timeRange === 'month') {
      const monthAgo = subDays(now, 30);
      return stateTests.filter(test => 
        isWithinInterval(parseISO(test.timestamp), { start: monthAgo, end: now })
      );
    }

    return stateTests;
  };

  const filteredTests = getFilteredTests();

  // Calculate statistics
  const totalTests = filteredTests.length;
  const passedTests = filteredTests.filter(test => test.result === 'passed').length;
  const failedTests = totalTests - passedTests;
  const passRate = totalTests > 0 ? Math.round((passedTests / totalTests) * 100) : 0;

  // Group tests by day for the chart
  const getTestsByDay = () => {
    const testsByDay: Record<string, { date: string, count: number, passed: number }> = {};

    filteredTests.forEach(test => {
      const day = test.timestamp.split('T')[0];
      if (!testsByDay[day]) {
        testsByDay[day] = { date: day, count: 0, passed: 0 };
      }
      testsByDay[day].count++;
      if (test.result === 'passed') {
        testsByDay[day].passed++;
      }
    });

    // Convert to array and sort by date
    return Object.values(testsByDay).sort((a, b) => 
      a.date.localeCompare(b.date)
    );
  };

  const testsByDay = getTestsByDay();

  // Calculate max count for scaling the chart
  const maxCount = Math.max(...testsByDay.map(day => day.count), 5);

  return (
    <div className="reality-prompt-stats">
      <div className="stats-header">
        <h3>
          <FiBarChart2 className="stats-icon" />
          Reality Check Statistics
        </h3>
        <div className="time-range-selector">
          <button 
            className={`range-button ${timeRange === 'week' ? 'active' : ''}`}
            onClick={() => setTimeRange('week')}
          >
            Week
          </button>
          <button 
            className={`range-button ${timeRange === 'month' ? 'active' : ''}`}
            onClick={() => setTimeRange('month')}
          >
            Month
          </button>
          <button 
            className={`range-button ${timeRange === 'all' ? 'active' : ''}`}
            onClick={() => setTimeRange('all')}
          >
            All Time
          </button>
        </div>
      </div>

      <div className="stats-summary">
        <div className="stat-card">
          <div className="stat-value">{totalTests}</div>
          <div className="stat-label">Total Checks</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{passRate}%</div>
          <div className="stat-label">Pass Rate</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{passedTests}</div>
          <div className="stat-label">Passed</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{failedTests}</div>
          <div className="stat-label">Failed</div>
        </div>
      </div>

      <div className="stats-chart">
        <h4>
          <FiCalendar />
          Daily Reality Checks
        </h4>

        {testsByDay.length > 0 ? (
          <div className="chart-container">
            <div className="chart-bars">
              {testsByDay.map(day => (
                <div key={day.date} className="chart-bar-group">
                  <div className="chart-bar-container">
                    <div 
                      className="chart-bar"
                      style={{ height: `${(day.count / maxCount) * 100}%` }}
                    >
                      <div 
                        className="chart-bar-passed"
                        style={{ height: `${(day.passed / day.count) * 100}%` }}
                      ></div>
                    </div>
                  </div>
                  <div className="chart-label">
                    {format(parseISO(day.date), 'MM/dd')}
                  </div>
                </div>
              ))}
            </div>

            <div className="chart-legend">
              <div className="legend-item">
                <div className="legend-color total"></div>
                <div className="legend-label">Total Checks</div>
              </div>
              <div className="legend-item">
                <div className="legend-color passed"></div>
                <div className="legend-label">Passed</div>
              </div>
            </div>
          </div>
        ) : (
          <div className="no-data">
            <p>No data available for the selected time range.</p>
          </div>
        )}
      </div>

      <div className="stats-history">
        <h4>
          <FiClock />
          Recent Reality Checks
        </h4>

        {filteredTests.length > 0 ? (
          <div className="history-list">
            {filteredTests.slice(0, 5).map(test => (
              <div key={test.id} className="history-item">
                <div className={`result-indicator ${test.result}`}></div>
                <div className="history-details">
                  <div className="history-time">
                    {format(parseISO(test.timestamp), 'MMM d, yyyy h:mm a')}
                  </div>
                  <div className="history-result">
                    {test.result === 'passed' ? 'Passed' : 'Failed'}
                    {test.notes && <span className="history-notes"> - {test.notes}</span>}
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-data">
            <p>No reality checks recorded in the selected time range.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default RealityPromptStats;
