<xbeshAction type="file" filePath="src/components/RealityPromptManager.tsx">import { useState } from 'react';
import { FiClock, FiVolume2, FiToggleRight, FiToggleLeft, FiSettings } from 'react-icons/fi';
import { useRealityPrompt } from '../context/RealityPromptContext';
import RealityPromptStats from './RealityPromptStats';
import '../styles/RealityPromptManager.css';

const RealityPromptManager = () => {
  const { settings, updateSettings, isEnabled, toggleEnabled, todaysTestCount } = useRealityPrompt();
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  const handleToggleEnabled = () => {
    toggleEnabled(!isEnabled);
  };

  const handleSettingsChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    if (name === 'dailyReminderCount') {
      updateSettings({
        ...settings,
        dailyReminderCount: parseInt(value)
      });
    } else if (name === 'alertType') {
      updateSettings({
        ...settings,
        alertType: value as 'sound' | 'vibrate' | 'voice'
      });
    } else if (name === 'startHour') {
      updateSettings({
        ...settings,
        activeHours: {
          ...settings.activeHours,
          start: value
        }
      });
    } else if (name === 'endHour') {
      updateSettings({
        ...settings,
        activeHours: {
          ...settings.activeHours,
          end: value
        }
      });
    }
  };

  return (
    <div className="reality-prompt-manager">
      <div className="manager-header">
        <div className="manager-title">
          <h2>Reality Prompt System</h2>
          <p className="manager-description">
            Random "Is this a dream?" prompts throughout the day to build awareness
          </p>
        </div>

        <div className="manager-controls">
          <div className="today-count">
            <span className="count-value">{todaysTestCount}</span>
            <span className="count-label">Today's Checks</span>
          </div>

          <button 
            className={`toggle-button ${isEnabled ? 'enabled' : 'disabled'}`}
            onClick={handleToggleEnabled}
          >
            {isEnabled ? (
              <>
                <FiToggleRight />
                <span>Enabled</span>
              </>
            ) : (
              <>
                <FiToggleLeft />
                <span>Disabled</span>
              </>
            )}
          </button>

          <button 
            className="settings-button"
            onClick={() => setIsSettingsOpen(!isSettingsOpen)}
          >
            <FiSettings />
            <span>Settings</span>
          </button>
        </div>
      </div>

      {isSettingsOpen && (
        <div className="settings-panel">
          <div className="settings-group">
            <label htmlFor="dailyReminderCount">
              <FiClock />
              Daily Reminder Count
            </label>
            <input
              type="range"
              id="dailyReminderCount"
              name="dailyReminderCount"
              min="1"
              max="30"
              value={settings.dailyReminderCount}
              onChange={handleSettingsChange}
            />
            <div className="range-value">{settings.dailyReminderCount} prompts per day</div>
          </div>

          <div className="settings-group">
            <label htmlFor="alertType">
              <FiVolume2 />
              Alert Type
            </label>
            <select
              id="alertType"
              name="alertType"
              value={settings.alertType}
              onChange={handleSettingsChange}
            >
              <option value="sound">Sound</option>
              <option value="vibrate">Vibration</option>
              <option value="voice">Voice Prompt</option>
            </select>
          </div>

          <div className="settings-group hours-group">
            <label>Active Hours</label>
            <div className="hours-inputs">
              <div className="hour-input">
                <span>From</span>
                <input
                  type="time"
                  name="startHour"
                  value={settings.activeHours.start}
                  onChange={handleSettingsChange}
                />
              </div>
              <div className="hour-input">
                <span>To</span>
                <input
                  type="time"
                  name="endHour"
                  value={settings.activeHours.end}
                  onChange={handleSettingsChange}
                />
              </div>
            </div>
          </div>
        </div>
      )}

      <RealityPromptStats />
    </div>
  );
};

export default RealityPromptManager;
</xbeshAction>

