<xbeshAction type="file" filePath="src/components/RealityPromptStats.tsx">import { useState, useEffect } from 'react';
import { FiCheckCircle, FiAlertCircle, FiCalendar } from 'react-icons/fi';
import { useRealityPrompt } from '../context/RealityPromptContext';
import '../styles/RealityPromptStats.css';

const RealityPromptStats = () => {
  const { stateTests } = useRealityPrompt();
  const [stats, setStats] = useState({
    total: 0,
    passed: 0,
    failed: 0,
    streak: 0,
    lastWeek: [] as number[]
  });

  useEffect(() => {
    if (stateTests.length === 0) return;

    // Calculate stats
    const passed = stateTests.filter(test => test.result === 'passed').length;
    const failed = stateTests.filter(test => test.result === 'failed').length;

    // Calculate streak
    let currentStreak = 0;
    for (let i = stateTests.length - 1; i >= 0; i--) {
      if (stateTests[i].result === 'passed') {
        currentStreak++;
      } else {
        break;
      }
    }

    // Calculate last week's tests
    const now = new Date();
    const weekData = Array(7).fill(0);

    stateTests.forEach(test => {
      const testDate = new Date(test.timestamp);
      const dayDiff = Math.floor((now.getTime() - testDate.getTime()) / (1000 * 60 * 60 * 24));

      if (dayDiff < 7) {
        weekData[6 - dayDiff]++;
      }
    });

    setStats({
      total: stateTests.length,
      passed,
      failed,
      streak: currentStreak,
      lastWeek: weekData
    });
  }, [stateTests]);

  const getDayLabel = (index: number) => {
    const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    const today = new Date().getDay();
    const dayIndex = (today - (6 - index) + 7) % 7;
    return days[dayIndex];
  };

  return (
    <div className="reality-prompt-stats">
      <div className="stats-header">
        <h3>Reality Check Statistics</h3>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-value">{stats.total}</div>
          <div className="stat-label">Total Checks</div>
        </div>

        <div className="stat-card">
          <div className="stat-value passed">{stats.passed}</div>
          <div className="stat-label">
            <FiCheckCircle /> Reality Confirmed
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-value failed">{stats.failed}</div>
          <div className="stat-label">
            <FiAlertCircle /> Dreamlike States
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-value">{stats.streak}</div>
          <div className="stat-label">Current Streak</div>
        </div>
      </div>

      <div className="weekly-chart">
        <div className="chart-header">
          <FiCalendar />
          <h4>Last 7 Days</h4>
        </div>

        <div className="chart-bars">
          {stats.lastWeek.map((count, index) => (
            <div key={index} className="chart-bar-container">
              <div 
                className="chart-bar" 
                style={{ 
                  height: count ? `${Math.min(100, count * 20)}%` : '5%',
                  backgroundColor: count ? 'var(--primary-color)' : 'var(--border-color)'
                }}
              >
                {count > 0 && <span className="bar-value">{count}</span>}
              </div>
              <div className="bar-label">{getDayLabel(index)}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RealityPromptStats;
</xbeshAction>

