const EducationalContent = () => {
  const [activeCategory, setActiveCategory] = useState<string>('all');
  const [expandedItem, setExpandedItem] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');

  const categories = [
    { id: 'all', label: 'All Content', icon: <FiBook /> },
    { id: 'meditation', label: 'Meditation', icon: <FiBook /> },
    { id: 'lucid-dreaming', label: 'Lucid Dreaming', icon: <FiBook /> },
    { id: 'dream-yoga', label: 'Dream Yoga', icon: <FiBook /> },
    { id: 'didgeridoo', label: 'Didgeridoo', icon: <FiBook /> }
  ];

  const filteredContent = CONTENT_DATA.filter(item => {
    const matchesCategory = activeCategory === 'all' || item.category === activeCategory;
    const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                          item.description.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const toggleExpand = (id: string) => {
    setExpandedItem(expandedItem === id ? null : id);
  };

  const handleOpenPdf = (url: string) => {
    window.open(url, '_blank');
  };

  return (
    <div className="educational-content">
      <div className="content-header">
        <h2>Educational Resources</h2>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search resources..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
      </div>

      <div className="category-tabs">
        {categories.map(category => (
          <button
            key={category.id}
            className={`category-tab ${activeCategory === category.id ? 'active' : ''}`}
            onClick={() => setActiveCategory(category.id)}
          >
            {category.icon}
            {category.label}
          </button>
        ))}
      </div>

      <div className="content-list">
        {filteredContent.length > 0 ? (
          filteredContent.map(item => (
            <motion.div
              key={item.id}
              className="content-item"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <div 
                className="content-item-header" 
                onClick={() => toggleExpand(item.id)}
              >
                <div className="content-icon">
                  {item.type === 'video' ? <FiVideo /> : <FiFileText />}
                </div>
                <div className="content-info">
                  <h3>{item.title}</h3>
                  <div className="content-meta">
                    <span className={`content-category ${item.category}`}>
                      {item.category.replace('-', ' ')}
                    </span>
                    {item.duration && <span className="content-duration">{item.duration}</span>}
                    {item.level && (
                      <span className={`content-level ${item.level}`}>
                        {item.level}
                      </span>
                    )}
                  </div>
                </div>
                <div className="expand-icon">
                  {expandedItem === item.id ? <FiChevronUp /> : <FiChevronDown />}
                </div>
              </div>

              {expandedItem === item.id && (
                <div className="content-details">
                  <p className="content-description">{item.description}</p>

                  {item.type === 'video' ? (
                    <div className="video-container">
                      <iframe
                        src={item.url}
                        title={item.title}
                        frameBorder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowFullScreen
                      ></iframe>
                    </div>
                  ) : (
                    <button 
                      className="pdf-button"
                      onClick={() => handleOpenPdf(item.url)}
                    >
                      <FiExternalLink /> Open PDF Document
                    </button>
                  )}
                </div>
              )}
            </motion.div>
          ))
        ) : (
          <div className="no-content">
            <p>No resources found matching your search criteria.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default EducationalContent;
</xbeshAction>
